ol input-sm" name="txtName1" type="text" placeholder="Item Name" required minlength="2">                                                <br />                                                <input class="form-control input-sm" name="txtDescription" type="text" placeholder="Item Description">                                                <br />                                                <div class="row">                                                    <div class="col-md-8">                                                        <input class="form-control ui-widget" name="drpType" id="drpType"   placeholder="Enter item type">                                                    </div>                                                    <div class="col-md-4">                                                        <button id="typeLink"  class=" btn btn-info" >Add New Type</button>                                                    </div>                                                </div>                                                <br />                                                <br />                                                <input class="form-control" name="txtQuantity"  type="text" placeholder="Item Quantity">                                                <br />                                                <div class="row">                                                    <div class="col-md-6">                                                        <input class="form-control  btn btn-danger"  type="reset" value="Clear All"/>                                                    </div>                                                    <div class="col-md-6">                                                        <input class="form-control  btn btn-info"  type="submit" value="Save Item">                                                    </div>                                                    <br />                                                </div>                                            </form>                                        </div>                                    </div>                                </ul>                            </div>                        </div>	                    </div>                </div>	                <!-- Modal -->                <div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">                    <div class="modal-dialog" role="document">                        <div class="modal-content">                            <div class="modal-header">                                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                                <h4 class="modal-title" id="myModalLabel">Add Item Type</h4>                            </div>                            <div class="modal-body">                                <div class="row">                                    <div class="col-md-12">                                        <!--<form action="<?php //echo base_url();                     ?>index.php/itemType/add" method="post" enctype="multipart/form-data">-->                                        <input class="form-control input-sm" name="txtName" id ="txtName" type="text" placeholder="Item Type">                                        <br />                                    </div>                                </div>                            </div>                            <div class="modal-footer">                                <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>                                <button type="button" class="btn btn-primary" id="btn-item-type" data-dismiss="modal">Save</button>                            </div>                        </div>                    </div>                </div>                </section>            </div>            <?php $this->load->view("footer"); ?>            <?php $this->load->view("rightsidebar"); ?>            <div class="control-sidebar-bg"></div>        </div>        <?php $this->load->view("header-js"); ?>        <script src="<?php echo base_url(); ?>/theme/plugins/datatables/jquery.dataTables.min.js"></script>        <script src="<?php echo base_url(); ?>/theme/plugins/datatables/dataTables.bootstrap.min.js"></script>        <script>            var item_type_add = false;            $(document).ready(function () {                $("#hello").hide();            });            $('#typeLink').click(function () {                $('#myModal').modal('show');            });            $("#btn-item-type").click(function () {                var type = $("#txtName").val();                var success1 = "<h6 class='success alert alert-success' id='message'>Item type Successfully added</h6>";                $.ajax({                    type: "POST",                    url: "<?php echo base_url(); ?>itemType/addModal",                    dataType: 'json',                    data: {type: type},                    success: function () {                        $("#txtName").val('');                        $("#hello").show();                        $(success1).appendTo("#hello");                    },                    error: function () {                        $(success1).appendTo("#hello");                        $("#hello").show();                        $("#message").fadeOut(4000)();                        alert("Something went wrong add");                    }                });            });            $("#drpType").keyup(function () {                ajaxItemTypeLoad();            });            //Drop down loading from here            function ajaxItemTypeLoad() {                $.ajax({                    type: "POST",                    url: "<?php echo base_url(); ?>index.php/item/itemTypeAjax",                    dataType: 'json',                    success: function (data) {                        var item_type = [];                        var dataArr = JSON.stringify(data);                        $.each(JSON.parse(dataArr), function (idx, obj) {                            item_type.push(obj.name);                        });                        $("#drpType").autocomplete({                            source: item_type                        });                    },                    error: function () {                        alert("Something went wrong");                    }                });            }        </script>    </body></html>