put class="form-control input-sm" name="txtDescription" type="text" placeholder="Item Description">                                                <br />                                                <div class="row">                                                    <div class="col-md-8">                                                        <select class="form-control" name="drpType" id="drpType"  type="text">                                                            <option value="Not listed">Not Listed</option>                                                            <option value="N/A">Not Available</option>                                                        </select>                                                    </div>                                                    <div class="col-md-4">                                                        <button id="typeLink"  class=" btn btn-info" data-toggle="modal" data-target="#myModal">Add Brand</button>                                                    </div>                                                </div>                                                <div class="row">                                                    <div class="col-md-12 success alert alert-success" id="hello">                                                                                                            </div>                                                </div>                                                <br />                                                <div class="row">                                                    <div class="col-md-8">                                                        <select class="form-control" name="drpBrand" id="drpBrand"  type="text" placeholder="Item Brand">                                                            <option value="Not listed">Not Listed</option>                                                            <option value="unbranded">Un-branded</option>                                                        </select>                                                    </div>                                                    <div class="col-md-4">                                                        <a id="brandLink" href="<?php echo base_url(); ?>index.php/itemBrand/" class=" btn btn-info">Add Brand</a>                                                    </div>                                                </div>                                                <br />                                                <input class="form-control" name="txtQuantity"  type="text" placeholder="Item Quantity">                                                <br />                                                <div class="row">                                                    <div class="col-md-6">                                                        <input class="form-control  btn btn-danger"  type="reset" value="Clear All"/>                                                    </div>                                                    <div class="col-md-6">                                                        <input class="form-control  btn btn-info"  type="submit" value="Save Item">                                                    </div>                                                    <br />                                                </div>                                            </form>                                        </div>                                    </div>                                </ul>                            </div>                        </div>	                    </div>                </div>	                <!-- Modal -->                <div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">                    <div class="modal-dialog" role="document">                        <div class="modal-content">                            <div class="modal-header">                                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                                <h4 class="modal-title" id="myModalLabel">Modal title</h4>                            </div>                            <div class="modal-body">                                <div class="row">                                    <div class="col-md-12">                                        <!--<form action="<?php //echo base_url();    ?>index.php/itemType/add" method="post" enctype="multipart/form-data">-->                                        <input class="form-control input-sm" name="txtName" id ="txtName" type="text" placeholder="Item Type">                                        <br />                                    </div>                                </div>                            </div>                            <div class="modal-footer">                                <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>                                <button type="button" class="btn btn-primary" id="btn-item-type" data-dismiss="modal">Save</button>                            </div>                        </div>                    </div>                </div>                </section>            </div>            <?php $this->load->view("footer"); ?>            <?php $this->load->view("rightsidebar"); ?>            <div class="control-sidebar-bg"></div>        </div>        <?php $this->load->view("header-js"); ?>        <script src="<?php echo base_url(); ?>/theme/plugins/datatables/jquery.dataTables.min.js"></script>        <script src="<?php echo base_url(); ?>/theme/plugins/datatables/dataTables.bootstrap.min.js"></script>        <script>            $(document).ready(function(){                $("#hello").hide();            });            $(function () {                                //Show add brand link                $("#drpBrand").change(function () {                    if ($("#drpBrand option:selected").text() == "Not Listed") {                        $("#brandLink").show();                    } else {                        $("#brandLink").hide();                    }                });                //Show add Type link                $("#drpType").change(function () {                    if ($("#drpType option:selected").text() == "Not Listed") {                        $("#typeLink").show();                    } else {                        $("#typeLink").hide();                    }                });                $('#tblItemDet').DataTable({                    "paging": true,                    "lengthChange": false,                    "searching": false,                    "ordering": true,                    "info": true,                    "autoWidth": false                });            });            $("#btn-item-type").click(function () {                var type = $("#txtName").val();//                alert(type);                $.ajax({                    type: "POST",                    url: "<?php echo base_url(); ?>itemType/addModal",                    dataType: 'json',                    data: {type: type, modal: 1},                    success: function (data) {//                        $('.text').text(JSON.stringify(data));//                        alert(JSON.stringify(data));//                        alert(data);                        $('#myModal').modal('show');                        $("#txtName").val('');                        var success = "Item type Successfully added";                        $("#hello").html(success);                        $("#hello").show();                    },                });            });        </script>    </body></html>